import Category from "../components/Category";
import { Footer } from "../components/Footer";
import { Header } from "../components/Header";

const backlogTasks = [
  {
    title: "backlog",
    issues: [
      {
        id: "12345",
        name: "Sprint bugfix",
        description: "Fix all the bugs",
      },
    ],
  },
  {
    title: "backlog",
    issues: [
      {
        id: "12345",
        name: "Sprint bugfix",
        description: "Fix all the bugs",
      },
    ],
  },
];

const Tasks = () => {
  return (
    <>
      <Header />
      <main>
        <div className="flex gap-3 px-3 bg-[#0079BF] h-[85vh] p-4">
          {backlogTasks.map((tasks) => (
            <Category tasks={tasks.issues} title={tasks.title} />
          ))}
        </div>
      </main>
      <Footer />
    </>
  );
};

export default Tasks;
