      {
        id: "12345",
        name: "Sprint bugfix",
        description: "Fix all the bugs",
      },
      {
        id: "12345",
        name: "Sprint bugfix",
        description: "Fix all the bugs",
      },
      {
        id: "12345",
        name: "Sprint bugfix",
        description: "Fix all the bugs",
      },
    ],
  },
];

const Tasks = () => {
  const [tasks, setTasks] = useState();

  useEffect(() => {
    setTasks(localStorage.getItem("tasks") ?? []);
  }, []);
  return (
    <>
      <Header />
      <main>
        <div className="flex gap-3 px-3 bg-[#0079BF] h-[85vh] py-4">
          {backlogTasks.map((tasks, index) => (
            <Category tasks={tasks.issues} title={tasks.title} key={index} />
          ))}
        </div>
      </main>
      <Footer />
    </>
  );
};

export default Tasks;
