import React from "react";

export const Footer = ({ activeTasks, finishedTasks }) => {
  return (
    <footer>
      <div className="h-[55px] px-4 py-1 bg-[#0067A3] flex justify-between text-white">
        <span>Active tasks: {activeTasks}</span>
        <span className="ml-3">Finished tasks: {finishedTasks}</span>
      </div>
    </footer>
  );
};
